 var spymode = true;
var that;
 
 function spyOn()
 {
     spymode = true;
     
     
     $('#navbar .nav li > a[data-target="'+that.activeTarget+'"],#navbar .nav li > a[href="'+that.activeTarget+'"]')
     .parent('li')
     .addClass('active')

     active = $('#navbar .nav li > a[data-target="javascript:hidePopUp()"],#navbar .nav li > a[href="javascript:hidePopUp()"]')
     .parent('.active')
     .removeClass('active')

 }

 function spyOff()
 {
     spymode = false;
     
     
     $(that.selector)
     .parent('.active')
     .removeClass('active')
//     alert("ok...");
     
     active = $('#navbar .nav li > a[data-target="javascript:hidePopUp()"],#navbar .nav li > a[href="javascript:hidePopUp()"]')
     .parent('li')
     .addClass('active')
 }


